import "core-js/modules/es.array.push.js";
import axios from 'axios';
import $ from 'jquery';
import { required } from 'vuelidate/lib/validators';
import { API_OBTENIR_REFERENCE_PAR_PAR_FAMILLE, API_RECUPERER_LISTE_OFFRE_VOYAGE } from '../globalConfig/globalConstConfig';
export default {
  name: "CreerModeTarif",
  data() {
    return {
      errorMsg: null,
      prixOffreVoyageToSend: {
        datas: []
      },
      prixOffreVoyageModel: {
        designation: null,
        prix: null,
        modeDesignation: null,
        categorieVoyageurDesignation: null,
        offreVoyageDesignation: null
      },
      referenceModeOffreVoyageList: [],
      objectToSend: {
        datas: []
      },
      referenceModeOffreVoyage: {
        referenceFamilleDesignation: "referenceFamilleMode"
      },
      offreVoyageParCompagnieTransportList: [],
      offreVoyageObject: {
        data: {
          compagnieTransportRaisonSociale: "COMPAGNIE KOUEVI CT"
        }
      }
    };
  },
  methods: {
    // RECUPERER LA LISTE DES OFFRES DE VOYAGES DISPONIBLES PAR COMPAGNIE DE TRANSPORT
    async obtenirOffreVoyageParCompagnieTransport() {
      axios.post(API_RECUPERER_LISTE_OFFRE_VOYAGE, this.offreVoyageObject).then(response => {
        if (response.status == 200) {
          if (response.data.status.code != 800) {
            this.errorMsg = response.data.status.message;
            $(".alert-error").fadeIn();
            setTimeout(function () {
              $(".alert-error").fadeOut();
            }, 4000);
          } else {
            this.offreVoyageParCompagnieTransportList = response.data.items;
            console.log(this.offreVoyageParCompagnieTransportList);
          }
        } else {
          this.errorMsg = "Erreur";
          $(".alert-error").fadeIn();
          setTimeout(function () {
            $(".alert-error").fadeOut();
          }, 4000);
        }
      }).catch(e => {
        this.errorMsg = e;
      });
    },
    //OBTENIR REFERENCE DESIGNATION TYPE OFFRE DE VOYAGE
    async obtenirReferenceModeOffreVoyage() {
      this.objectToSend.datas.push(this.referenceModeOffreVoyage);
      await axios.post(API_OBTENIR_REFERENCE_PAR_PAR_FAMILLE, this.objectToSend).then(response => {
        this.referenceModeOffreVoyageList = response.data.items;
      }).catch(e => {
        this.errorMsg = e;
        $(".alert-error").fadeIn();
        setTimeout(function () {
          $(".alert-error").fadeOut();
        }, 4000);
      });
    }
  },
  mounted() {
    this.obtenirReferenceModeOffreVoyage();
    this.obtenirOffreVoyageParCompagnieTransport();
  }
};
