import "core-js/modules/es.array.push.js";
import axios from 'axios';
import $ from 'jquery';
import { required } from 'vuelidate/lib/validators';
import { API_CREER_VILLE } from '../globalConfig/globalConstConfig';
export default {
  name: 'CreerVille',
  data() {
    return {
      overlay: false,
      errorMsg: null,
      warningMsg: null,
      successMsg: null,
      objectContainList: {
        datas: []
      },
      ville: {
        designation: null,
        description: null
      }
    };
  },
  validations: {
    ville: {
      designation: {
        required
      }
    }
  },
  methods: {
    // CREATION D'UNE VILLE VIA UN SERVICE WEB
    async creerVille() {
      this.objectContainList.datas.push(this.ville);
      await axios.post(API_CREER_VILLE, this.objectContainList).then(response => {
        if (response.status == 200) {
          if (response.data.status.code == 800) {
            this.successMsg = response.data.status.message;
            $(".alert-success").fadeIn();
            setTimeout(function () {
              $(".alert-success").fadeOut();
            }, 4000);
          } else {
            this.errorMsg = response.data.status.message;
            $(".alert-error").fadeIn();
            setTimeout(function () {
              $(".alert-error").fadeOut();
            }, 3000);
          }
        } else if (response.status == 204) {
          this.warningMsg = "Erreur , lors de la création";
          $(".alert-warning").fadeIn();
          setTimeout(function () {
            $(".alert-warning").fadeOut();
          }, 3000);
        } else {
          this.errorMsg = "Erreur , opération de création impossible";
          $(".alert-error").fadeIn();
          setTimeout(function () {
            $(".alert-error").fadeOut();
          }, 3000);
        }
      }).catch(e => {
        this.errorMsg = e;
        $(".alert-error").fadeIn();
        setTimeout(function () {
          $(".alert-error").fadeOut();
        }, 3000);
      }).finally(() => {
        this.overlay = false;
      });
    },
    // ENVOI DU FORMULAIRE VERS LE MIDDLEWARE

    submitForm() {
      this.$v.$touch();
      if (this.$v.ville.$invalid) {
        this.errorMsg = 'Des informations sont manquantes';
        $(".alert-error").fadeIn();
        setTimeout(function () {
          $(".alert-error").fadeOut();
        }, 3000);
      } else {
        this.creerVille();
      }
    }
  },
  computed: {
    // ENSEMBLE DES CONTRAINTES POUR L'ENRGISTREMENT D'UN PAYS
    designationVilleErrors() {
      const errors = [];
      if (!this.$v.ville.designation.$dirty) return errors;
      !this.$v.ville.designation.required && errors.push('Veuillez saisir une désignation.');
      return errors;
    }
  }
};
