import axios from "axios";
import $ from 'jquery';
import { API_OBTENIR_LISTE_DES_VILLES_DISPONIBLE } from '../globalConfig/globalConstConfig';
export default {
  name: 'ModifierVilleEscaleOffreVoyage',
  data() {
    return {
      errorMsg: null,
      successMsg: null,
      overlay: false,
      objectValue: {},
      villesList: [],
      villeEscaleModel: {
        id: null,
        position: null,
        designation: null
      }
    };
  },
  methods: {
    //ENREGISTRER LES MODIFICATIONS PORTANT SUR UNE VILLE D'ESCALE

    // MODE EDITION D'UNE VILLE ESCALE
    async editerVilleEscale() {
      if (localStorage.getItem("villeEscaleEdit")) {
        try {
          const villeEscaleEditing = JSON.parse(localStorage.getItem("villeEscaleEdit"));
          this.villeEscaleModel.id = villeEscaleEditing.id;
          this.villeEscaleModel.designation = villeEscaleEditing.designation;
          this.villeEscaleModel.position = villeEscaleEditing.position;
          console.log(villeEscaleEditing);
          localStorage.removeItem("villeEscaleEdit");
        } catch (error) {
          localStorage.removeItem("villeEscaleEdit");
        }
      } else {
        this.$router.replace({
          path: '/selectionnerOffreVoyage'
        });
      }
    },
    //RECUPERER LA LISTE DES VILLES DISPONIBLES
    async obtenirVillesListApi() {
      await axios.post(API_OBTENIR_LISTE_DES_VILLES_DISPONIBLE, this.objectValue).then(response => {
        this.villesList = response.data.items;
      }).catch(e => {
        this.errorMsg = e;
        $(".alert-error").fadeIn();
        setTimeout(function () {
          $(".alert-error").fadeOut();
        }, 4000);
      });
    }
  },
  computed: {},
  mounted() {
    this.obtenirVillesListApi();
    this.editerVilleEscale();
  }
};
