import axios from 'axios';
import $ from 'jquery';
import { API_OBTENIR_MODE_ABONNEMENT_PAR_COMPAGNIE, API_OBTENIR_MODE_PAIEMENT_PAR_COMPAGNIE } from '../globalConfig/globalConstConfig';
export default {
  name: 'SelectionnerModeAbonnementEtPaiement',
  data() {
    return {
      errorMsg: null,
      loading: true,
      search: '',
      headers: [{
        text: 'reference',
        value: 'id'
      }, {
        text: 'Designation',
        value: 'designation'
      }, {
        text: 'Type Abonnement',
        value: 'typeModeAbonnementDesignation'
      }, {
        text: 'Addresse',
        value: 'adresseLocalisation'
      }, {
        text: 'Telephone',
        value: 'telephone1'
      }, {
        text: 'Actions',
        value: 'actions',
        sortable: false
      }],
      abonnementDisponibleList: [],
      abonnementListObject: {
        data: {
          compagnieTransportRaisonSociale: "COMPAGNIE KOUEVI CT"
        }
      },
      paiementDisponibleList: [],
      paiementListObject: {
        data: {
          compagnieTransportRaisonSociale: "COMPAGNIE KOUEVI CT"
        }
      }
    };
  },
  methods: {
    // RECUPERER LA LISTE DES MODES D'ABONNEMENTS ENREGISTRÉS PAR COMPAGNIE DE TRANSPORT
    async obtenirAbondemmentListParCompagnieTransport() {
      this.loading = true;
      axios.post(API_OBTENIR_MODE_ABONNEMENT_PAR_COMPAGNIE, this.abonnementListObject).then(response => {
        if (response.status == 200) {
          if (response.data.status.code != 800) {
            this.errorMsg = response.data.status.message;
            $(".alert-error").fadeIn();
            setTimeout(function () {
              $(".alert-error").fadeOut();
            }, 4000);
          } else {
            this.abonnementDisponibleList = response.data.items;
          }
        } else {
          this.errorMsg = "Erreur";
          $(".alert-error").fadeIn();
          setTimeout(function () {
            $(".alert-error").fadeOut();
          }, 4000);
        }
      }).catch(e => {
        this.errorMsg = e;
      }).finally(() => {
        this.loading = false;
      });
    },
    // RECUPERER LA LISTE DES MODES DE PAIEMENT DISPONIBLE PAR COMPAGNIE DE TRANSPORT
    async obtenirPaiementListParCompagnieTransport() {
      this.loading = true;
      axios.post(API_OBTENIR_MODE_PAIEMENT_PAR_COMPAGNIE, this.paiementListObject).then(response => {
        if (response.status == 200) {
          if (response.data.status.code != 800) {
            this.errorMsg = response.data.status.message;
            $(".alert-error").fadeIn();
            setTimeout(function () {
              $(".alert-error").fadeOut();
            }, 4000);
          } else {
            this.paiementDisponibleList = response.data.items;
          }
        } else {
          this.errorMsg = "Erreur";
          $(".alert-error").fadeIn();
          setTimeout(function () {
            $(".alert-error").fadeOut();
          }, 4000);
        }
      }).catch(e => {
        this.errorMsg = e;
      }).finally(() => {
        this.loading = false;
      });
    }
  },
  mounted() {
    this.obtenirAbondemmentListParCompagnieTransport();
    this.obtenirPaiementListParCompagnieTransport();
  }
};
