import "core-js/modules/es.array.push.js";
import { API_RESET_PASSWORD_USER } from '../globalConfig/globalConstConfig';
import axios from 'axios';
import { required } from 'vuelidate/lib/validators';
export default {
  name: "changerPassword",
  data() {
    return {
      errorMsg: null,
      successMsg: null,
      overlay: null,
      oldPasswordIsVisible: false,
      newPasswordIsVisible: false,
      userParams: {
        email: null,
        oldPassword: null,
        newPassword: null
      },
      userReset: {
        data: {
          email: null,
          oldPassword: null,
          newPassword: null
        }
      }
    };
  },
  validations: {
    userReset: {
      oldPassword: {
        required
      },
      newPassword: {
        required
      }
    }
  },
  methods: {
    async retrieveUserInfo() {
      /**
       * REFAIRE LA RECUPERATION DES DONNEES UTILISATEURS
       */
      if (localStorage.getItem('userLoggedSocieteMere')) {
        const parsedUserLogged = JSON.parse(localStorage.getItem('userLoggedSocieteMere'));
        console.log(parsedUserLogged);
        this.userReset.email = parsedUserLogged.email;
      }
    },
    // SOUMISSION DU FORMULAIRE
    submitForm() {
      this.$v.$touch();
      if (this.$v.userReset.$invalid) this.errorMsg = "Vous avez omis des champs de saisie";else this.resetPasswordUser();
    },
    //CHANGER DE MOT DE PASSE
    async resetPasswordUser() {
      this.overlay = true;
      this.userReset.data.email = this.userReset.email;
      this.userReset.data.oldPassword = this.userReset.oldPassword;
      this.userReset.data.newPassword = this.userReset.newPassword;
      await axios.post(API_RESET_PASSWORD_USER, this.userReset).then(response => {
        if (response.status == 200) {
          if (response.data.status.code == 800) {
            this.successMsg = response.data.status.message;
            this.$swal.fire('Mot de Passe modifié', this.successMsg, 'success');
            this.userReset.data.email = null;
            this.userReset.data.oldPassword = null;
            this.userReset.data.newPassword = null;
          } else {
            this.errorMsg = response.data.status.message;
            this.$swal.fire('Erreur', this.errorMsg, 'error');
            this.userReset.data.email = null;
            this.userReset.data.oldPassword = null;
            this.userReset.data.newPassword = null;
          }
        } else if (response.status == 204) {
          this.warningMsg = "Erreur , lors de la création";
          this.$swal.fire('Erreur', this.warningMsg, 'warning');
          this.userReset.data.email = null;
          this.userReset.data.oldPassword = null;
          this.userReset.data.newPassword = null;
        } else {
          this.errorMsg = "Erreur , opération de création impossible";
          this.$swal.fire('Erreur', this.errorMsg, 'error');
          this.userReset.data.email = null;
          this.userReset.data.oldPassword = null;
          this.userReset.data.newPassword = null;
        }
      }).catch(e => {
        this.$swal.fire('Erreur', e, 'error');
        this.userReset.data.email = null;
        this.userReset.data.oldPassword = null;
        this.userReset.data.newPassword = null;
      }).finally(() => {
        this.overlay = false;
      });
    }
  },
  computed: {
    //GESTION DES CHAMPS UTILISATEURS

    oldPasswordError() {
      const errors = [];
      if (!this.$v.userReset.oldPassword.$dirty) return errors;
      !this.$v.userParams.oldPassword.required && errors.push('Entrer l\'ancien mot de passe.');
      return errors;
    },
    newPasswordError() {
      const errors = [];
      if (!this.$v.userR.newPassword.$dirty) return errors;
      !this.$v.userParams.newPassword.required && errors.push('Le nouveau mot de passe est obligatoire.');
      return errors;
    }
  },
  mounted() {
    this.retrieveUserInfo();
  }
};
