import "core-js/modules/es.array.push.js";
import { required } from 'vuelidate/lib/validators';
import axios from "axios";
import $ from 'jquery';
import { API_OBTENIR_REFERENCE_PAR_PAR_FAMILLE } from '../globalConfig/globalConstConfig';
export default {
  name: "GestionBagageCompagnieTransport",
  data() {
    return {
      errorMsg: null,
      successMsg: null,
      overlay: false,
      bagageModel: {
        designation: null,
        coutBagageParTypeBagage: null,
        nombreBagageGratuitParTypeVoyage: null,
        typeBagageDesignation: null,
        compagnieTransportRaisonSociale: "COMPAGNIE KOUEVI CT"
      },
      referenceBagageList: [],
      referenceBagage: {
        referenceFamilleDesignation: "referenceFamilleBagage"
      },
      objectToSendReferenceBagage: {
        datas: []
      }
    };
  },
  validations: {
    bagageModel: {
      designation: {
        required
      },
      coutBagageParTypeBagage: {
        required
      },
      nombreBagageGratuitParTypeVoyage: {
        required
      },
      typeBagageDesignation: {
        required
      }
    }
  },
  methods: {
    //SOUMISSION DU FORMULAIRE
    submitForm() {},
    //CREER UNE INSTANCE DE BAGAGE
    async creerBagage() {
      this.prixOffreVoyageToSend.datas.push(this.prixOffreVoyageModel);
      this.overlay = true;
      await axios.post(API_RATTACHER_PRIX_OFFRE_VOYAGE, this.prixOffreVoyageToSend).then(response => {
        if (response.status == 200) {
          if (response.data.status.code == 800) {
            this.successMsg = response.data.status.message;
            $(".alert-success").fadeIn();
            setTimeout(function () {
              $(".alert-success").fadeOut();
            }, 4000);
            this.prixOffreVoyageToSend.datas = [];
          } else {
            this.errorMsg = response.data.status.message;
            $(".alert-error").fadeIn();
            setTimeout(function () {
              $(".alert-error").fadeOut();
            }, 3000);
            this.prixOffreVoyageToSend.datas = [];
          }
        } else if (response.status == 204) {
          this.warningMsg = "Erreur , lors de la création de l'offre de voyage";
          $(".alert-warning").fadeIn();
          setTimeout(function () {
            $(".alert-warning").fadeOut();
          }, 3000);
          this.prixOffreVoyageToSend.datas = [];
        } else {
          this.errorMsg = "Erreur , opération de création impossible";
          $(".alert-error").fadeIn();
          setTimeout(function () {
            $(".alert-error").fadeOut();
          }, 3000);
          this.prixOffreVoyageToSend.datas = [];
        }
      }).catch(e => {
        this.errorMsg = e;
        $(".alert-error").fadeIn();
        setTimeout(function () {
          $(".alert-error").fadeOut();
        }, 4000);
        this.prixOffreVoyageToSend.datas = [];
      }).finally(() => {
        this.overlay = false;
      });
    },
    //OBTENIR REFERENCE DESIGNATION BAGAGE
    async obtenirReferenceBagageList() {
      this.objectToSendReferenceBagage.datas.push(this.referenceBagage);
      await axios.post(API_OBTENIR_REFERENCE_PAR_PAR_FAMILLE, this.objectToSendReferenceBagage).then(response => {
        this.referenceBagageList = response.data.items;
      }).catch(e => {
        this.errorMsg = e;
        $(".alert-error").fadeIn();
        setTimeout(function () {
          $(".alert-error").fadeOut();
        }, 4000);
      });
    },
    //VERIFIE SI CEST UN NOMBRE POSITIF
    isNumber(item) {
      return item > 0;
    }
  },
  computed: {
    // GESTION DES CONTRAINTES DE CHAMPS D'ENTREES POUR LA CREATION DU MODE D'UNE OFFRE

    designationBagage() {
      const errors = [];
      if (!this.$v.bagageModel.designation.$dirty) return errors;
      !this.$v.bagageModel.designation.required && errors.push('La désignation est obligatoire.');
      return errors;
    },
    prixBagageErrors() {
      const errors = [];
      if (!this.$v.bagageModel.coutBagageParTypeBagage.$dirty) return errors;
      !this.isNumber(this.bagageModel.coutBagageParTypeBagage) && errors.push('Saisissez un prix correct.');
      !this.$v.bagageModel.coutBagageParTypeBagage.required && errors.push('Le prix est obligatoire.');
      return errors;
    },
    typeBagageErrors() {
      const errors = [];
      if (!this.$v.bagageModel.typeBagageDesignation.$dirty) return errors;
      !this.$v.bagageModel.typeBagageDesignation.required && errors.push('La selection du type du sac est obligatoire.');
      return errors;
    },
    nombreBagageErrors() {
      const errors = [];
      !this.isNumber(this.bagageModel.nombreBagageGratuitParTypeVoyage) && errors.push('Saisissez un nombre correct.');
      if (!this.$v.bagageModel.nombreBagageGratuitParTypeVoyage.$dirty) return errors;
      !this.$v.bagageModel.nombreBagageGratuitParTypeVoyage.required && errors.push('Veuillez renseigner ce champs.');
      return errors;
    }
  },
  mounted() {
    this.obtenirReferenceBagageList();
  }
};
