import "core-js/modules/es.array.push.js";
import { required } from 'vuelidate/lib/validators';
import axios from 'axios';
import $ from 'jquery';
import { API_OBTENIR_REFERENCE_PAR_PAR_FAMILLE } from '../globalConfig/globalConstConfig';
export default {
  name: "CreerModeAbonnement",
  data() {
    return {
      errorMsg: null,
      successMsg: null,
      overlay: false,
      menuDebutAbonnement: false,
      menuFinAbonnement: false,
      date: new Date(Date.now() - new Date().getTimezoneOffset() * 60000).toISOString().substr(0, 10),
      referenceAbonnementList: [],
      referenceAbonnement: {
        referenceFamilleDesignation: "referenceFamillePeriodiciteAbonnement"
      },
      objectToSendReferenceAbonnement: {
        datas: []
      },
      modeAbonnementCompagnieModel: {
        designation: null,
        dateDebutAbonnement: null,
        dateFinAbonnement: null,
        redevance: null,
        redevancePublicite: null,
        typeModeAbonnementDesignation: null,
        periodiciteAbonnementDesignation: null,
        compagnieTransportRaisonSociale: "COMPAGNIE KOUEVI CT"
      }
    };
  },
  validations: {
    modeAbonnementCompagnieModel: {
      designation: {
        required
      },
      dateDebutAbonnement: {
        required
      },
      dateFinAbonnement: {
        required
      },
      redevance: {
        required
      },
      redevancePublicite: {
        required
      },
      typeModeAbonnementDesignation: {
        required
      },
      periodiciteAbonnementDesignation: {
        required
      }
    }
  },
  methods: {
    // SOUMISSION DU FORMULAIRE
    submitForm() {},
    async creerAbonnementCompagnie() {},
    //OBTENIR REFERENCE DESIGNATION BAGAGE
    async obtenirReferenceAbonnementList() {
      this.objectToSendReferenceAbonnement.datas.push(this.referenceAbonnement);
      await axios.post(API_OBTENIR_REFERENCE_PAR_PAR_FAMILLE, this.objectToSendReferenceAbonnement).then(response => {
        this.referenceBagageList = response.data.items;
      }).catch(e => {
        this.errorMsg = e;
        $(".alert-error").fadeIn();
        setTimeout(function () {
          $(".alert-error").fadeOut();
        }, 4000);
      });
    }
  },
  computed: {
    // GESTION DES CONTRAINTES DE CHAMPS D'ENTREES POUR LA CREATION D'UN ABONNEMENT

    designationAbonnementErrors() {
      const errors = [];
      if (!this.$v.modeAbonnementCompagnieModel.designation.$dirty) return errors;
      !this.$v.modeAbonnementCompagnieModel.designation.required && errors.push('La désignation est obligatoire.');
      return errors;
    },
    redevanceAbonnementErrors() {
      const errors = [];
      if (!this.$v.bagageModel.coutBagageParTypeBagage.$dirty) return errors;
      !this.isNumber(this.bagageModel.coutBagageParTypeBagage) && errors.push('Saisissez un prix correct.');
      !this.$v.bagageModel.coutBagageParTypeBagage.required && errors.push('Le prix est obligatoire.');
      return errors;
    },
    typeBagageErrors() {
      const errors = [];
      if (!this.$v.bagageModel.typeBagageDesignation.$dirty) return errors;
      !this.$v.bagageModel.typeBagageDesignation.required && errors.push('La selection du type du sac est obligatoire.');
      return errors;
    },
    nombreBagageErrors() {
      const errors = [];
      !this.isNumber(this.bagageModel.nombreBagageGratuitParTypeBagage) && errors.push('Saisissez un nombre correct.');
      if (!this.$v.bagageModel.nombreBagageGratuitParTypeBagage.$dirty) return errors;
      !this.$v.bagageModel.nombreBagageGratuitParTypeBagage.required && errors.push('Veuillez renseigner ce champs.');
      return errors;
    }
  },
  mounted() {
    this.obtenirReferenceAbonnementList();
  }
};
