        if (response.status == 200) {
          if (response.data.status.code != 800) {
            this.errorMsg = response.data.status.message;
            $(".alert-error").fadeIn();
            setTimeout(function () {
              $(".alert-error").fadeOut();
            }, 4000);
          } else {
            this.compagnieTransportList = response.data.items;
          }
        } else {
          this.errorMsg = "Erreur";
        }
      }).catch(e => {
        this.errorMsg = e;
      });
    }
  },
  mounted() {
    this.obtenirDemandeAdhesionValidees();
  }
