import "core-js/modules/es.array.push.js";
import { required } from 'vuelidate/lib/validators';
import $ from 'jquery';
import axios from 'axios';
import { API_CREER_PAYS } from '../globalConfig/globalConstConfig';
export default {
  name: "CreerPays",
  data() {
    return {
      overlay: false,
      errorMsg: null,
      successMsg: null,
      warningMsg: null,
      objectContainList: {
        datas: []
      },
      pays: {
        designation: null,
        description: null
      }
    };
  },
  validations: {
    pays: {
      designation: {
        required
      }
    }
  },
  methods: {
    // CREATION D'UN PAYS VIA UN SERVICE WEB
    async creerPays() {
      this.objectContainList.datas.push(this.pays);
      await axios.post(API_CREER_PAYS, this.objectContainList).then(response => {
        if (response.status == 200) {
          if (response.data.status.code == 800) {
            this.successMsg = response.data.status.message;
            $(".alert-success").fadeIn();
            setTimeout(function () {
              $(".alert-success").fadeOut();
            }, 4000);
          } else {
            this.errorMsg = response.data.status.message;
            $(".alert-error").fadeIn();
            setTimeout(function () {
              $(".alert-error").fadeOut();
            }, 3000);
          }
        } else if (response.status == 204) {
          this.warningMsg = "Erreur , lors de la création";
          $(".alert-warning").fadeIn();
          setTimeout(function () {
            $(".alert-warning").fadeOut();
          }, 3000);
        } else {
          this.errorMsg = "Erreur , opération de création impossible";
          $(".alert-error").fadeIn();
          setTimeout(function () {
            $(".alert-error").fadeOut();
          }, 3000);
        }
      }).catch(e => {
        this.errorMsg = e;
        $(".alert-error").fadeIn();
        setTimeout(function () {
          $(".alert-error").fadeOut();
        }, 3000);
      }).finally(() => {
        this.overlay = false;
      });
    },
    // SOUMISSION DU FORMULAIRE

    submitForm() {
      this.$v.$touch();
      if (this.$v.pays.$invalid) {
        this.errorMsg = 'Des informations sont manquantes';
        $(".alert-error").fadeIn();
        setTimeout(function () {
          $(".alert-error").fadeOut();
        }, 3000);
      } else {
        this.creerPays();
      }
    }
  },
  computed: {
    // ENSEMBLE DES CONTRAINTES POUR L'ENRGISTREMENT D'UN PAYS
    designationPaysErrors() {
      const errors = [];
      if (!this.$v.pays.designation.$dirty) return errors;
      !this.$v.pays.designation.required && errors.push('Veuillez saisir une désignation.');
      return errors;
    }
  }
};
